mapper.select:
select job_id, company_id, title, description, requirements, salary_range, location, status, create_time, update_time from tb_job_position
status = #{status}
order by create_time desc

select job_id, company_id, title, description, requirements, salary_range, location, status, create_time, update_time from tb_job_position where company_id = #{companyId}
and status = #{status}
order by create_time desc

mapper.update:
update tb_job_position
title = #{title}, description = #{description}, requirements = #{requirements}, salary_range = #{salaryRange}, location = #{location}, status = #{status}, update_time = #{updateTime}
where job_id = #{jobId}
